// src/components/VagasTable.js
//import React, { useState, useEffect } from 'react';
//import { Table, Button } from 'react-bootstrap';
//import axios from 'axios';

import React from 'react';
import { Table, Button } from 'react-bootstrap';
import axios from 'axios';

const VagasTable = () => {
  const handleInscricao = async (vagaId) => {
    try {
      const candidatoId = 'ID_DO_CANDIDATO'; // Você precisa obter o ID do candidato logado
      await axios.post('http://localhost:4000/api/candidato_vaga', { candidatoId, vagaId });
      alert('Inscrição realizada com sucesso!');
      // Atualize a lista de vagas após a inscrição
      // fetchVagas();
    } catch (error) {
      console.error('Erro ao se inscrever na vaga:', error);
      alert('Erro ao se inscrever na vaga. Por favor, tente novamente.');
    }
  };

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>Código</th>
          <th>Salário</th>
          <th>Cidade</th>
          <th>Quantidade</th>
          <th>Ações</th> {/* Adicionado */}
        </tr>
      </thead>
      <tbody>
        {vagas.map((vaga) => (
          <tr key={vaga.pk_vaga_codigo}>
            <td>{vaga.vaga_codigo}</td>
            <td>{vaga.vaga_salario}</td>
            <td>{vaga.vaga_cidade}</td>
            <td>{vaga.vaga_quantidade}</td>
            <td>
              <Button variant="primary" onClick={() => handleInscricao(vaga.pk_vaga_codigo)}>
                Inscrever-se
              </Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default VagasTable;
