// src/App.js
import React, { useState, useEffect } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import CandidatosTable from './components/CandidatosTable.jsx';
import VagasTable from './components/VagasTable';
import MyComponent from './components/MyComponent';

function App() {
  const [formData, setFormData] = useState({
    nome: '',
    endereco: '',
    telefone: ''
  });

  const [candidatos, setCandidatos] = useState([]);

  useEffect(() => {
    // Função para buscar os candidatos ao carregar a página
    fetchCandidatos();
  }, []);

  const fetchCandidatos = async () => {
    try {
      const response = await fetch('http://seu-backend.com/api/candidatos');
      if (!response.ok) {
        throw new Error('Erro ao obter os candidatos');
      }
      const data = await response.json();
      setCandidatos(data);
    } catch (error) {
      console.error('Erro ao obter os candidatos:', error.message);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://seu-backend.com/api/candidatos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });
      if (!response.ok) {
        throw new Error('Erro ao adicionar candidato');
      }
      alert('Candidato adicionado com sucesso!');
      // Após adicionar o candidato com sucesso, atualiza a lista de candidatos
      fetchCandidatos();
    } catch (error) {
      console.error('Erro ao adicionar candidato:', error.message);
      alert('Erro ao adicionar candidato. Por favor, tente novamente.');
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  return (
    <div>
      <MyComponent />
      <Container>
        <h1>Adicionar Candidato</h1>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formNome">
            <Form.Label>Nome</Form.Label>
            <Form.Control
              type="text"
              placeholder="Nome do candidato"
              name="nome"
              value={formData.nome}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group controlId="formEndereco">
            <Form.Label>Endereço</Form.Label>
            <Form.Control
              type="text"
              placeholder="Endereço do candidato"
              name="endereco"
              value={formData.endereco}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group controlId="formTelefone">
            <Form.Label>Telefone</Form.Label>
            <Form.Control
              type="text"
              placeholder="Telefone do candidato"
              name="telefone"
              value={formData.telefone}
              onChange={handleChange}
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Adicionar
          </Button>
        </Form>
        <h1>Candidatos</h1>
        <CandidatosTable candidatos={candidatos} />
        <h1>Vagas</h1>
        <VagasTable />
      </Container>
    </div>
  );
}

export default App;
