import React, { useState } from 'react';

const CandidatoForm = () => {
  const [candidato, setCandidato] = useState({
    nome: '',
    endereco: '',
    telefone: ''
  });

  const [enviado, setEnviado] = useState(false);
  const [erro, setErro] = useState('');

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCandidato({ ...candidato, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Validar os campos antes de enviar
    if (!candidato.nome || !candidato.endereco || !candidato.telefone) {
      setErro('Por favor, preencha todos os campos.');
      return;
    }

    // Simular envio para o backend
    try {
      // Aqui você pode adicionar a lógica para enviar os dados para o backend
      console.log('Enviando candidato:', candidato);
      setEnviado(true);
      setErro('');
    } catch (error) {
      console.error('Erro ao enviar candidato:', error);
      setErro('Erro ao enviar candidato. Por favor, tente novamente mais tarde.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {enviado && <p>Formulário enviado com sucesso!</p>}
      {erro && <p style={{ color: 'red' }}>{erro}</p>}
      <div>
        <label htmlFor="nome">Nome:</label>
        <input
          type="text"
          id="nome"
          name="nome"
          value={candidato.nome}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="endereco">Endereço:</label>
        <input
          type="text"
          id="endereco"
          name="endereco"
          value={candidato.endereco}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="telefone">Telefone:</label>
        <input
          type="text"
          id="telefone"
          name="telefone"
          value={candidato.telefone}
          onChange={handleChange}
        />
      </div>
      <button type="submit">Adicionar Candidato</button>
    </form>
  );
};

export default CandidatoForm;
