// src/App.js
import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import CandidatosTable from './components/CandidatosTable.jsx';
import VagasTable from './components/VagasTable';
import CandidatoForm from './components/CandidatoForm';

function App() {
  const [candidatos, setCandidatos] = useState([]);

  useEffect(() => {
    fetchCandidatos();
  }, []);

  const fetchCandidatos = async () => {
    try {
      const response = await fetch('http://localhost:4000/api/candidatos'); // Altere o endereço para o seu backend
      if (!response.ok) {
        throw new Error('Erro ao obter os candidatos');
      }
      const data = await response.json();
      setCandidatos(data);
    } catch (error) {
      console.error('Erro ao obter os candidatos:', error.message);
    }
  };

  const handleSubmit = async (formData) => {
    try {
      const response = await fetch('http://localhost:4000/api/candidatos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });
      if (!response.ok) {
        throw new Error('Erro ao adicionar candidato');
      }
      alert('Candidato adicionado com sucesso!');
      fetchCandidatos();
    } catch (error) {
      console.error('Erro ao adicionar candidato:', error.message);
      alert('Erro ao adicionar candidato. Por favor, tente novamente.');
    }
  };

  return (
    <div>
      <Container>
        <h1>Adicionar Candidato</h1>
        <CandidatoForm onSubmit={handleSubmit} />
        <h1>Candidatos</h1>
        <CandidatosTable candidatos={candidatos} />
        <h1>Vagas</h1>
        <VagasTable />
      </Container>
    </div>
  );
}

export default App;
