// src/App.js
import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import CandidatosTable from './components/CandidatosTable.jsx';
import VagasTable from './components/VagasTable';
import MyComponent from './components/MyComponent';
import CandidatoForm from './components/CandidatoForm'; // Adicionado
import VagasForm from './components/VagasForm'; // Adicionado

function App() {
  const [candidatos, setCandidatos] = useState([]);

  useEffect(() => {
    fetchCandidatos();
  }, []);

  const fetchCandidatos = async () => {
    try {
      const response = await fetch('http://localhost:4000/api/candidatos');
      if (!response.ok) {
        throw new Error('Erro ao obter os candidatos');
      }
      const data = await response.json();
      setCandidatos(data);
    } catch (error) {
      console.error('Erro ao obter os candidatos:', error.message);
    }
  };

  const handleCandidatoAddSuccess = () => {
    fetchCandidatos(); // Chamamos fetchCandidatos após adicionar um candidato
  };

  const handleVagaAddSuccess = () => {
    fetchCandidatos(); // Chamamos fetchCandidatos após adicionar uma vaga
  };

  return (
    <div>
      <MyComponent />
      <Container>
        <h1>Adicionar Candidato</h1>
        <CandidatoForm onAddSuccess={handleCandidatoAddSuccess} /> {/* Passamos a função handleCandidatoAddSuccess como prop */}
        <h1>Candidatos</h1>
        <CandidatosTable candidatos={candidatos} /> {/* Passa os candidatos como prop para CandidatosTable */}
        <h1>Adicionar Vaga</h1>
        <VagasForm onAddSuccess={handleVagaAddSuccess} /> {/* Passamos a função handleVagaAddSuccess como prop */}
        <h1>Vagas</h1>
        <VagasTable />
      </Container>
    </div>
  );
}

export default App;
