// src/App.js
import React, { useState, useEffect } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import CandidatosTable from './components/CandidatosTable.jsx';
import VagasTable from './components/VagasTable'; // Importe o componente VagasTable
import MyComponent from './components/MyComponent';
import CandidatoForm from './components/CandidatoForm'; // Adicionado

function App() {
  const [candidatos, setCandidatos] = useState([]);
  const [vagas, setVagas] = useState([]); // Adicione o estado para as vagas

  useEffect(() => {
    fetchCandidatos();
    fetchVagas(); // Chame a função para obter as vagas
  }, []);

  const fetchCandidatos = async () => {
    try {
      const response = await fetch('http://4000/candidatos');
      if (!response.ok) {
        throw new Error('Erro ao obter os candidatos');
      }
      const data = await response.json();
      setCandidatos(data);
    } catch (error) {
      console.error('Erro ao obter os candidatos:', error.message);
    }
  };

  const fetchVagas = async () => { // Função para obter as vagas do backend
    try {
      const response = await fetch('http://4000/vagas');
      if (!response.ok) {
        throw new Error('Erro ao obter as vagas');
      }
      const data = await response.json();
      setVagas(data);
    } catch (error) {
      console.error('Erro ao obter as vagas:', error.message);
    }
  };

  const handleSubmit = async (formData) => { // Recebe os dados do formulário
    try {
      const response = await fetch('http://4000/candidatos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });
      if (!response.ok) {
        throw new Error('Erro ao adicionar candidato');
      }
      alert('Candidato adicionado com sucesso!');
      fetchCandidatos();
    } catch (error) {
      console.error('Erro ao adicionar candidato:', error.message);
      alert('Erro ao adicionar candidato. Por favor, tente novamente.');
    }
  };

  return (
    <div>
      <MyComponent />
      <Container>
        <h1>Adicionar Candidato</h1>
        <CandidatoForm onSubmit={handleSubmit} /> {/* Passa a função handleSubmit para o componente CandidatoForm */}
        <h1>Candidatos</h1>
        <CandidatosTable candidatos={candidatos} /> {/* Passa os candidatos como prop para CandidatosTable */}
        <h1>Vagas</h1>
        <VagasTable vagas={vagas} /> {/* Passa as vagas como prop para VagasTable */}
      </Container>
    </div>
  );
}

export default App;
