// src/components/CandidatosTable.js
import React, { useState, useEffect } from 'react';
import { Table, Button } from 'react-bootstrap';
import axios from 'axios';

const CandidatosTable = () => {
  const [candidatos, setCandidatos] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/candidatos');
        setCandidatos(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchData();
  }, []);

  const handleEditar = (candidato) => {
    // Implemente a lógica para editar o candidato
    console.log('Editar candidato:', candidato);
  };

  const handleExcluir = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/candidatos/${id}`);
      const response = await axios.get('http://localhost:4000/api/candidatos');
      setCandidatos(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>CPF</th>
          <th>Nome</th>
          <th>Endereço</th>
          <th>Telefone</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        {candidatos.map((candidato) => (
          <tr key={candidato.cand_cpf}>
            <td>{candidato.cand_cpf}</td>
            <td>{candidato.cand_nome}</td>
            <td>{candidato.cand_endereco}</td>
            <td>{candidato.cand_telefone}</td>
            <td>
              <Button variant="primary" onClick={() => handleEditar(candidato)}>Editar</Button>
              <Button variant="danger" onClick={() => handleExcluir(candidato.id)}>Excluir</Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default CandidatosTable;
